#!/usr/bin/env ruby

test_data ="  "

class Fiber
  include Enumerable
  def each
    loop { yield self.resume }
  end
end

def palindrome_gen(a)
  Fiber.new {
    last_idx = a.size - 1
    0.upto(last_idx-1) do |i|
      (i+1).upto(last_idx) do |j|
        substr = a[i..j]
        Fiber.yield substr if substr == substr.reverse
      end
    end
    raise StopIteration
  }
end

puts palindrome_gen(test_data).max_by {|word| word.size }
